window.addEventListener("load", () => {
  window.addEventListener("scroll", () => {
    let e = document.querySelector(".header"),
      t = e.offsetHeight;
    window.scrollY >= t ? e.classList.add("_header-scroll") : e.classList.remove("_header-scroll");
  }),
    setTimeout(() => {
      (function e() {
        let e = document.querySelectorAll(".spollers");
        e.forEach((e) => {
          e.addEventListener("click", (e) => {
            let t = (self = e.currentTarget).querySelector(".spoller"),
              l = self.querySelector(".card-service__top"),
              s = self.querySelector(".icon-arrow-down");
            self.classList.toggle("spollers-parent"),
              t.classList.toggle("open"),
              s && s.classList.toggle("open"),
              l && l.classList.toggle("open"),
              t.style.maxHeight ? (t.style.maxHeight = null) : (t.style.maxHeight = t.scrollHeight + "px");
          });
        });
      })(),
        (function e() {
          let t = document.querySelector(".video__body");
          t.addEventListener("click", (e) => {
            let l = e.currentTarget,
              s = l.querySelector(".video__image"),
              o = l.querySelector(".video__bottom"),
              i = l.querySelector(".video__info");
            if (l.classList.contains("playing")) return;
            l.classList.add("playing"), s.classList.add("playing"), o.classList.add("playing"), i.classList.add("playing");
            let r = t.dataset.videoSrc;
            l.insertAdjacentHTML(
              "afterbegin",
              '<iframe src="https://www.youtube.com/embed/' +
                r +
                '?autoplay=1" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>',
            );
          });
        })(),
        new Swiper(".swiper", { direction: "horizontal", loop: !0, autoplay: !0, pagination: { el: ".swiper-pagination" } });
    }, 2e3),
    (function e() {
      let t = document.querySelectorAll("[data-animation]"),
        l = new IntersectionObserver((e) => {
          e.forEach((e) => {
            e.isIntersecting ? e.target.classList.add("show") : e.target.classList.remove("show");
          });
        });
      t.forEach((e) => {
        l.observe(e);
      });
    })(),
    (function e() {
      let t = document.querySelector(".icon-menu"),
        l = document.querySelector("body"),
        s = document.querySelector(".menu__body");
      t &&
        (t.addEventListener("click", function (e) {
          let t = document.body.style.top;
          s.classList.toggle("_active"),
            document.documentElement.classList.toggle("menu-open"),
            s.classList.contains("_active")
              ? ((l.style.position = "fixed"), (t = `-${window.scrollY}px`))
              : ((l.style.position = ""), (l.style.top = ""), window.scrollTo(0, -1 * parseInt(scrollY || "0")));
        }),
        s.addEventListener("click", (e) => {
          e.target.classList.contains("menu__link") &&
            (s.classList.remove("_active"), (l.style.position = ""), document.documentElement.classList.remove("menu-open"));
        }));
    })();
});
